<?phpclass MageProfis_ManufacturerCategories_Block_Productview extends MageProfis_ManufacturerCategories_Block_Abstract {    /**     * retrieve current product from registry     * @return Object      */    public function getCurrentProduct() {        return Mage::registry('current_product');    }         /**     * Get Manufacturer Category for current product by category path     * @return object || false     */    public function getBrand()    {        foreach($this->getCurrentProduct()->getCategoryCollection()->getData() as $_category)        {                        $parent = '/' . trim((string)$this->getManufacturerCategoryId()) . '/';            if(strpos($_category['path'], $parent))            {                $id = end(explode('/', trim($_category['path'], '/')));                $category = Mage::getModel('catalog/category')->getCollection()                                            ->addAttributeToSelect('*')                                            ->addIdFilter($id)                                            ->getFirstItem();                return $category;            }        }        return false;    }        /**     * return "" if module is not activated in settings     * @return      */    protected function _toHtml() {        if (!Mage::getStoreConfigFlag('manufacturercategories/product/active')) {            return '';        }        return parent::_toHtml();    }}